  display: flex;
  flex-direction: column;
  gap: var(--spacing-xs);
}

.regItem label {
  font-size: var(--font-size-sm);
  color: var(--color-text-gray);
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

