function Header() {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`${styles.header} ${isScrolled ? styles.headerScrolled : ''}`}>
      <nav className={styles.nav}>
        <Link to="/" className={styles.logoLink}>
          <img src={siteLogo} alt="AUST CSE Carnival" className={styles.logo} />
        </Link>
        <div className={`${styles.menu} ${isMobileMenuOpen ? styles.menuOpen : ''}`}>
          {navLinks.map(link => (
            <NavLink
              key={link.name}
              to={link.path}
              className={({ isActive }) =>
                `${styles.menuItem} ${isActive ? styles.menuItemActive : ''}`
              }
            >
              {link.name}
            </NavLink>
          ))}
        </div>
        <button
          className={styles.hamburger}
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          aria-label="Toggle menu"
          aria-expanded={isMobileMenuOpen}
        >
          <span className={styles.hamburgerBar}></span>
          <span className={styles.hamburgerBar}></span>
          <span className={styles.hamburgerBar}></span>
        </button>
      </nav>
    </header>
  );
}

function Hero() {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % heroImages.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + heroImages.length) % heroImages.length);
  };

  useEffect(() => {
    const timer = setInterval(nextSlide, 5000);
    return () => clearInterval(timer);
  }, []);

  return (
    <section className={styles.hero}>
      <div className={styles.heroContent}>
        <h1 className={styles.heroTitle}>
          Experience the Thrilling AUST CSE Carnival
        </h1>
        <p className={styles.heroSubtitle}>
          Join us for a day of innovation and fun!
        </p>
        <div className={styles.heroButtons}>
          <Link to="/event" className={`${styles.button} ${styles.primaryButton}`}>
            Learn More
          </Link>
          <Link to="/register" className={`${styles.button} ${styles.secondaryButton}`}>
            Register
          </Link>
        </div>
      </div>
      <div className={styles.heroCarousel}>
        <img 
          src={heroImages[currentSlide]} 
          alt={`Carnival highlight ${currentSlide + 1}`}
          className={styles.carouselImage}
        />
        <div className={styles.carouselControls}>
          <button 
            onClick={prevSlide} 
            className={styles.carouselButton}
            aria-label="Previous slide"
          >
            ←
          </button>
          <button 
            onClick={nextSlide} 
            className={styles.carouselButton}
            aria-label="Next slide"
          >
            →
          </button>
        </div>
      </div>
    </section>
  );
}

