import React, { useState, useEffect } from 'react';
import { Link, NavLink } from 'react-router-dom';
import styles from './Header.module.css';
import siteLogo from '../assets/images/site_logo.png';

const navLinks = [
  { name: 'Home', path: '/' },
  { name: 'Event', path: '/event' },
  { name: 'Gallery', path: '/gallery' },
  { name: 'Sponsor', path: '/sponsor' },
  { name: 'Contact', path: '/contact' }
];

export default function Header() {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`${styles.header} ${isScrolled ? styles.headerScrolled : ''}`}>
      <nav className={styles.nav}>
        <Link to="/" className={styles.logoLink}>
          <img src={siteLogo} alt="AUST CSE Carnival" className={styles.logo} />
        </Link>
        <div className={`${styles.menu} ${isMobileMenuOpen ? styles.menuOpen : ''}`}>
          {navLinks.map(link => (
            <NavLink
              key={link.name}
              to={link.path}
              className={({ isActive }) =>
                `${styles.menuItem} ${isActive ? styles.menuItemActive : ''}`
              }
            >
              {link.name}
            </NavLink>
          ))}
          <div className={styles.authButtons}>
            <Link to="/signin" className={`${styles.button} ${styles.outlineButton}`}>
              Sign In
            </Link>
            <Link to="/signup" className={`${styles.button} ${styles.primaryButton}`}>
              Sign Up
            </Link>
          </div>
        </div>
        <button
          className={styles.hamburger}
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          aria-label="Toggle menu"
          aria-expanded={isMobileMenuOpen}
        >
          <span className={styles.hamburgerBar}></span>
          <span className={styles.hamburgerBar}></span>
          <span className={styles.hamburgerBar}></span>
        </button>
      </nav>
    </header>
  );
}

/* Header & Navigation */
