const navLinks = [
  { name: 'Home', path: '/' },
  { name: 'Event', path: '/event' },
  { name: 'Gallery', path: '/gallery' },
  { name: 'Sponsor', path: '/sponsor' },
  { name: 'Contact', path: '/contact' }
];

const socialLinks = [
  {
    name: 'Facebook',
    icon: FiFacebook,
    url: 'https://facebook.com/austcse',
    color: '#1877F2'
  },
