  },
  {
    name: 'LinkedIn',
    icon: FiLinkedin,
    url: 'https://linkedin.com/company/aust-cse',
    color: '#0A66C2'
  }
];

const contactInfo = [
  {
    icon: FiMail,
    label: 'Email',
