import React, { useState, useEffect } from 'react';
import { Link, NavLink } from 'react-router-dom';
import styles from './Home.module.css';
import Header from '../components/header/Header';

// Import images with correct paths from your structure
import siteLogo from '../assets/images/site_logo.png';
import titleSponsorLogo from '../assets/images/title_sponsor.jpg';
import organizerLogo from '../assets/images/organizer and co-organizer/organizer_operand.png';
import coOrganizerLogo from '../assets/images/organizer and co-organizer/co_organizer_perceptron.jpg';

// Hero carousel images
const heroImages = [
  './assets/images/event page hero image gallery/carnival_1_img_13.jpg',
  './assets/images/event page hero image gallery/carnival_3_img_17.jpg',
  './assets/images/event page hero image gallery/carnival_5_img_32.jpg'
];

const navLinks = [
  { name: 'Home', path: '/' },
  { name: 'Event', path: '/event' },
  { name: 'Gallery', path: '/gallery' },
  { name: 'Sponsor', path: '/sponsor' },
  { name: 'Contact', path: '/contact' }
];

const features = [
  {
    icon: '',
    title: 'Main Events',
    description: 'Exciting competitions and showcases throughout the carnival.'
  },
  {
    icon: '',
    title: 'Activities',
    description: 'Engage in various tech-focused activities and challenges.'
  },
  {
    icon: '',
    title: 'Competitions',
    description: 'Participate in multiple competitive events and win prizes.'
  }
];

const segments = [
  'UI/UX Competition',
  'Math Olympiad',
  'Capture The Flag',
  'Quiz',
  'Hackathon',
  'Chess',
  'E-Sports',
  'Programming Contest'
];

function Hero() {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % heroImages.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + heroImages.length) % heroImages.length);
  };

  useEffect(() => {
    const timer = setInterval(nextSlide, 5000);
    return () => clearInterval(timer);
  }, []);

  return (
    <section className={styles.hero}>
      <div className={styles.heroContent}>
        <h1 className={styles.heroTitle}>
          Experience the Thrilling AUST CSE Carnival
        </h1>
        <p className={styles.heroSubtitle}>
          Join us for a day of innovation and fun!
        </p>
        <div className={styles.heroButtons}>
          <Link to="/event" className={`${styles.button} ${styles.primaryButton}`}>
            Learn More
          </Link>
          <Link to="/register" className={`${styles.button} ${styles.secondaryButton}`}>
            Register
          </Link>
        </div>
      </div>
      <div className={styles.heroCarousel}>
        <img
          src={heroImages[currentSlide]}
          alt={`Carnival highlight ${currentSlide + 1}`}
          className={styles.carouselImage}
        />
        <div className={styles.carouselControls}>
          <button
            onClick={prevSlide}
            className={styles.carouselButton}
            aria-label="Previous slide"
          >
            ←
          </button>
          <button
            onClick={nextSlide}
            className={styles.carouselButton}
            aria-label="Next slide"
          >
            →
          </button>
        </div>
      </div>
    </section>
  );
}

function FeatureCard({ icon, title, description }) {
  return (
    <div className={styles.featureCard}>
      <span className={styles.featureIcon} role="img" aria-label={title}>
        {icon}
      </span>
      <h3 className={styles.featureTitle}>{title}</h3>
      <p className={styles.featureDescription}>{description}</p>
    </div>
  );
}

function Discover() {
  return (
    <section className={styles.discover}>
      <h2 className={styles.sectionTitle}>
        Experience the Thrills of AUST CSE Carnival
      </h2>
      <p className={styles.sectionDescription}>
        Dive into an extraordinary celebration of technology, innovation, and creativity.
        Join us for exciting competitions, workshops, and unforgettable moments.
      </p>
      <div className={styles.featureCards}>
        {features.map((feature) => (
          <FeatureCard key={feature.title} {...feature} />
        ))}
      </div>
    </section>
  );
}

function CTASection() {
  return (
    <section className={styles.ctaSection}>
      <h2 className={styles.ctaTitle}>Join the exciting CSE Carnival!</h2>
      <div className={styles.ctaButtons}>
        <Link to="/register" className={`${styles.button} ${styles.primaryButton}`}>
          Register Now
        </Link>
        <Link to="/event" className={`${styles.button} ${styles.outlineButton}`}>
          View Events
        </Link>
      </div>
    </section>
  );
}

function SponsorSection() {
  return (
    <section className={styles.sponsorSection}>
      <div className={styles.sponsorGrid}>
        <div className={styles.sponsorLogoContainer}>
          <img src={titleSponsorLogo} alt="MTB Title Sponsor" className={styles.sponsorLogo} />
        </div>
        <div className={styles.sponsorContent}>
          <h2 className={styles.sponsorTitle}>Our Title Sponsor MTB</h2>
          <p className={styles.sponsorDescription}>
            We are proud to partner with MTB, a leading organization committed to
            fostering innovation and supporting the next generation of tech leaders.
          </p>
          <Link to="/sponsor" className={`${styles.button} ${styles.sponsorButton}`}>
            Learn More
          </Link>
        </div>
      </div>
    </section>
  );
}

function OrganizerSection() {
  return (
    <section className={styles.organizerSection}>
      <h2 className={styles.sectionTitle}>The Event Organizers</h2>
      <div className={styles.organizerGrid}>
        <div className={styles.organizerCard}>
          <img src={organizerLogo} alt="AUSTPIC Logo" className={styles.organizerLogo} />
          <h3 className={styles.organizerName}>
            AUST Programming and Informatics Club
          </h3>
          <p className={styles.organizerDescription}>
            Main organizer of CSE Carnival, dedicated to fostering programming
            excellence and technical innovation among students.
          </p>
          <Link to="/about" className={styles.organizerLink}>Learn More</Link>
        </div>
        <div className={styles.organizerCard}>
          <img src={coOrganizerLogo} alt="Perceptron Logo" className={styles.organizerLogo} />
          <h3 className={styles.organizerName}>Perceptron</h3>
          <p className={styles.organizerDescription}>
            Co-organizing the carnival to bring their expertise in artificial intelligence
            and machine learning to the event.
          </p>
          <Link to="/about" className={styles.organizerLink}>Learn More</Link>
        </div>
      </div>
    </section>
  );
}

function Footer() {
  return (
    <footer className={styles.footer}>
      <div className={styles.footerGrid}>
        <div className={styles.footerCol}>
          <img src={siteLogo} alt="AUST CSE Carnival" className={styles.footerLogo} />
          <h3 className={styles.footerTitle}>Tribute Corner</h3>
          <Link to="/tribute" className={styles.footerLink}>In memory of our mentors</Link>
          <Link to="/gallery" className={styles.footerLink}>Past Carnivals</Link>
        </div>

        <div className={styles.footerCol}>
          <h3 className={styles.footerTitle}>Segments</h3>
          <div className={styles.segmentLinks}>
            {segments.map(segment => (
              <Link
                key={segment}
                to={`/segments/${segment.toLowerCase().replace(/\s+/g, '-')}`}
                className={styles.footerLink}
              >
                {segment}
              </Link>
            ))}
          </div>
        </div>

        <div className={styles.footerCol}>
          <h3 className={styles.footerTitle}>About Us</h3>
          <Link to="/about" className={styles.footerLink}>Our Story</Link>
          <Link to="/team" className={styles.footerLink}>Team</Link>
          <Link to="/contact" className={styles.footerLink}>Contact</Link>
        </div>

        <div className={styles.footerCol}>
          <h3 className={styles.footerTitle}>Follow Us</h3>
          <div className={styles.socialLinks}>
            <a
              href="https://facebook.com"
              target="_blank"
              rel="noopener noreferrer"
              aria-label="Facebook"
              className={styles.socialLink}
            >
              <i className="fab fa-facebook"></i>
            </a>
            <a
              href="https://instagram.com"
              target="_blank"
              rel="noopener noreferrer"
              aria-label="Instagram"
              className={styles.socialLink}
            >
              <i className="fab fa-instagram"></i>
            </a>
          </div>
          <a href="mailto:contact@austcsecarnival.com" className={styles.footerLink}>
            contact@austcsecarnival.com
          </a>
        </div>
      </div>
      <div className={styles.footerBottom}>
        <p>&copy; {new Date().getFullYear()} AUST CSE Carnival. All rights reserved.</p>
      </div>
    </footer>
  );
}

export default function Home() {
  return (
    <div className={styles.container}>
      <Header />
      <main>
        <Hero />
        <Discover />
        <CTASection />
        <SponsorSection />
        <OrganizerSection />
      </main>
      <Footer />
    </div>
  );
}

