import React, { useState, useEffect, useRef, useCallback } from 'react';
import Header from '../../components/header/Header';
import Footer from '../../components/footer/Footer';
import Icon from '../../components/common/Icon';
import styles from './Gallery.module.css';

// Import carnival images with proper error handling and correct paths
const importCarnivalImages = (carnivalVersion, count) => {
  const images = [];
  for (let i = 1; i <= count; i++) {
    images.push({
      src: `./assets/images/carnivals/Carnival ${carnivalVersion}.0/carnival_${carnivalVersion}_img_${i}.jpg`,
      alt: `AUST CSE Carnival ${carnivalVersion}.0 - Memory ${i}`,
      id: `carnival_${carnivalVersion}_${i}`,
      thumbnail: `./assets/images/carnivals/Carnival ${carnivalVersion}.0/carnival_${carnivalVersion}_img_${i}.jpg`
    });
  }
  return images;
};

// Enhanced carnival data with comprehensive information
